interface ICredits {
  cast: Cast[];
  crew: Crew[];
  id: number;
}

interface ISearchCrew {
  results: Crew[];
}

interface Cast {
  adult: boolean;
  character: string;
  credit_id: string;
  gender: number;
  id: number;
  known_for_department: string;
  name: string;
  order: number;
  original_name: string;
  popularity: number;
  profile_path: string;
}

interface Crew {
  adult: boolean;
  credit_id: string;
  department: string;
  gender: number;
  id: number;
  job: string;
  known_for_department: string;
  name: string;
  original_name: string;
  popularity: number;
  profile_path: string | null;
}

interface IPerson {
  adult: boolean;
  also_known_as: string[];
  biography: null | string;
  birthday: string;
  deathday: null | string;
  gender: number;
  homepage: string;
  id: number;
  imdb_id: string;
  known_for_department: string;
  name: string;
  place_of_birth: string;
  popularity: number;
  profile_path: string;
}
